import { Component } from "@angular/core";
import { FormGroup, FormControl, Validators, FormBuilder, ReactiveFormsModule } from "@angular/forms";
import { ActivatedRoute, Router } from "@angular/router";
import { FolderService } from "../services/folder.service";

@Component({
  selector: "app-folder-folderform",
  templateUrl: "./folderform.component.html"
})

export class FolderformComponent {
   folderForm: FormGroup;
  isEditMode: boolean = false;
  folderId: number = -1;

  constructor(
    private _formbuilder: FormBuilder,
    private _router: Router,
    private _route: ActivatedRoute,
    private _folderService: FolderService
  ) {
    // Initialize folderForm with form controls and validators
    this.folderForm = _formbuilder.group({
      folderName: ["", [
        Validators.required,
        Validators.minLength(2),
        Validators.maxLength(15),
        Validators.pattern(/^[0-9a-zA-ZæøåÆØÅ. -]{2,15}$/)
      ]],
      folderDescription: ["", [Validators.maxLength(150)]],
    });
  }

  // Handle form submission
  onSubmit() {
    const newFolder = this.folderForm.value;
    if (this.isEditMode) {
      // Update existing folder
      this._folderService.updateFolder(this.folderId, newFolder)
        .subscribe(response => {
          if (response.success) {
            console.log("Folder updated successfully");
            this._router.navigate(["/folder/" + this.folderId]);
          } else {
            console.log("Folder update failed");
          }
        });
    } else {
      // Create a new folder
      this._folderService.createFolder(newFolder)
        .subscribe(response => {
          if (response.success) {
            console.log(response.message);
            this._router.navigate(["/library"]);
          } else {
            console.log("Folder creation failed");
          }
        });
    }
  }

  // Navigate back to folders based on the edit mode
  backToFolders() {
    if (this.isEditMode) {
      this._router.navigate(["/folder/" + this.folderId]);
    } else {
      this._router.navigate(["/library"]);
    }
  }

  // Initialize component
  ngOnInit(): void {
    this._route.params.subscribe(params => {
      // Check the mode parameter to determine whether it's in create or update mode
      if (params["mode"] === "create") {
        this.isEditMode = false; // Create mode
      } else if (params["mode"] === "update") {
        this.isEditMode = true; // Edit mode
        this.folderId = +params["id"];
        this.loadItemForEdit(this.folderId);
      }
    });
    // Perform folder form validation
    this.validationFolder();
  }

  loadItemForEdit(folderId: number): void {
    // Call the getFolderById method of the folder service to fetch folder details.
    this._folderService.getFolderById(folderId)
      .subscribe({
        // Success callback (next): Invoked when folder details are successfully retrieved.
        next: (folder: any) => {
          // Update the folderForm with the retrieved folder details.
          this.folderForm.patchValue({
            folderName: folder.FolderName,
            folderDescription: folder.FolderDescription
          });
        },
        // Error callback: Invoked when an error occurs during the fetch operation.
        error: (error: any) => {
          console.error("Error loading folder for edit: ", error);
        }
      });
  }

  validationFolder() {
    const myInput: HTMLInputElement | null = document.getElementById("folderName") as HTMLInputElement;
    const validatationFolder: HTMLElement | null = document.getElementById("validatationFolder");
    if (validatationFolder) {
      validatationFolder.style.display = "none";
    }

    if (myInput && validatationFolder) {
      myInput.addEventListener("input", () => {
        validatationFolder.style.display = "block"
      });
      setTimeout(() => {
        validatationFolder.style.display = "block";
      }, 10000);
    }
  }
}
<section class="w-100 vh-100 mt-5 create-folder">
  <!-- Container for the create folder section -->
  <div class="container d-flex w-75 justify-content-between border border-1 p-0 shadow-sm bg-body rounded">
    <!-- Right section for creating a new folder -->
    <div class="p-5 w-100">
      <h2>{{isEditMode ? 'Update' : 'Create'}} Folder</h2>
      <hr />
      <form [formGroup]="folderForm" (ngSubmit)="onSubmit()">
        <!-- Input field for FolderName -->
        <div class="form-outline mb-4">
          <label for="folderName" class="form-label"></label>
          <input id="folderName" formControlName="folderName" type="text" class="form-control" autocomplete="off" placeholder="Input a folder name" />
        </div>
        <div id="validatationFolder">
          <div *ngIf="folderForm.invalid" class="text-danger">
            Write 2 - 15 characters long and do not use special character.
          </div>
        </div>
        <!-- Textarea for FolderDescription -->
        <div class="form-outline mb-4">
          <label class="form-label" for="folderDescription"></label>
          <textarea class="form-control" id="folderDescription" formControlName="folderDescription" rows="4" placeholder="Write something about your folder ....."></textarea>
        </div>
        <!-- Submit and Return buttons -->
        <div class="form-outline mb-4 d-flex justify-content-between">
          <button type="submit" class="btn btn-success btn-block mb-1 me-1"
                  [disabled]="folderForm.invalid">
            <i class="fa-solid fa-check"></i>
          </button>

          <a class="btn btn-secondary btn-block mb-1"
             (click)="backToFolders()">
            <i class="fa-solid fa-arrow-rotate-left"></i>
          </a>
        </div>
      </form>
    </div>
    <!-- Left section with background color and quote -->
    <div class="d-none d-md-block text-center w-50" style="background-color: #BCEAD5">
      <div class="p-5">
        <i class="fa-solid fa-folder-open fa-9x mt-5 mb-3" style="color: #8EC3B0;"></i>
        <figure>
          <blockquote class="blockquote">
            <p> “Clutter is nothing more than postponed decisions.”</p>
          </blockquote>
          <figcaption class="blockquote-footer">
            <cite title="Source Title">Barbara Hemphill</cite>
          </figcaption>
        </figure>
      </div>
    </div>
  </div>
</section>
