import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { DeckService } from '../services/deck.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {

  constructor(
    private _router: Router,
    private _deckService: DeckService,
    private _route: ActivatedRoute) { }

  navigateToDeckDemoOne() {
    this._router.navigate(['/flashcardone/', 1]).then(() => {
      window.scrollTo(0, 0);
    });
  }

  navigateToDeckDemoTwo() {
    this._router.navigate(['/flashcardone/', 2]).then(() => {
      window.scrollTo(0, 0);
    });
  }

  ngOnInit(): void {
    const flashcards = document.querySelectorAll('.flashcard-example-content') as NodeListOf<HTMLElement>;
    const flashcardContentObserver = new IntersectionObserver((entries) => {
      // add an animation when the flashcard example content section is revealed
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          entry.target.classList.add('flashcard-example-animation');
        }
      });
    }, {
      threshold: 0.01
    });
    flashcards.forEach((flashcard: HTMLElement) => {
      flashcardContentObserver.observe(flashcard);
    });

    const shape = document.querySelectorAll('.shape') as NodeListOf<HTMLElement>;
    const shapeContentObserver = new IntersectionObserver((entries) => {
      // every time the shapes from the home button are seen, add animation and remove animation if not
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          entry.target.classList.add('shape-animation');
        } else {
          entry.target.classList.remove('shape-animation');
        }
      });
    }, {
      threshold: 0.01
    });
    shape.forEach((shapeElement: HTMLElement) => {
      shapeContentObserver.observe(shapeElement);
    });
  }
}
<!--- main --->
<!-- Welcome Section -->
<section class="welcome-container">
  <div class="welcome-text">
    <h2>Flashcard app</h2>
    <a class="btn btn-create-flashcard"
       [routerLink]="['/deckform', 'create', null]">
      Start Now
    </a>
  </div>
  <!-- Shape Icons -->
  <div class="shape shape-one"><i class="fa-solid fa-clover fa-8x" style="color: #def5e5;"></i></div>
  <div class="shape shape-two"><i class="fa-solid fa-heart fa-8x" style="color: #def5e5;"></i></div>
  <div class="shape shape-three"><i class="fa-solid fa-square fa-8x" style="color: #def5e5;"></i></div>
  <div class="shape shape-four"><i class="fa-solid fa-star fa-8x" style="color: #def5e5;"></i></div>
</section>

<!-- Flashcard Type Introduction Section -->
<section class="flashcard-type-intro">
  <div>
    <h1>The Journey</h1>
    <p>
      Unlock the door to effective learning and personal growth with our flashcard app.
      Don't miss this opportunity to make education an exciting adventure.

      Ready to begin your learning journey? Sign-up and start exploring. Your path to success is just a click away.
    </p>
    <a class="btn btn-create-flashcard text-decoration-none link-dark" href="#demo">Try Demo</a>
  </div>
</section>

<!-- Flashcard Example Container Section -->
<section class="flashcard-example-container" id="demo">
  <div class="flashcard-example">
    <div class="flashcard-example-content">
      <!-- Food Quiz Demo Card -->
      <a class="card border border-0 text-decoration-none link-dark"
         (click)="navigateToDeckDemoTwo()">
        <img src="../../assets/img/food.jpeg" class="card-img-top" alt="Fissure in Sandstone" />
        <div class="card-body">
          <h5 class="card-title">Food Quiz Demo</h5>
          <p class="card-text">One cannot think well, love well, sleep well, if one has not dined well.</p>
        </div>
      </a>
    </div>
  </div>
  <div class="flashcard-example">
    <div class="flashcard-example-content">
      <!-- History Quiz Demo Card -->
      <a class="card border border-0 text-decoration-none link-dark"
         (click)="navigateToDeckDemoOne()">
        <img src="../../assets/img/history.jpg" class="card-img-top" alt="Fissure in Sandstone" />
        <div class="card-body">
          <h5 class="card-title">History Quiz Demo</h5>
          <p class="card-text">A generation which ignores history has no past and no future.</p>
        </div>
      </a>
    </div>
  </div>
</section>

